// HTML-Content der urspr√ºnglichen E-Mail extrahieren
const input = $input.first().json;
let emailHtml = input.textHtml || input.html || input.text || input.textPlain || '';

console.log('Extrahiere Link direkt aus der E-Mail...');

// Suche nach dem _rte/information Link in der E-Mail
const linkPattern = /href="(https:\/\/www\.kaiseraugst\.ch\/_rte\/information\/\d+)"/i;
const match = emailHtml.match(linkPattern);

if (match && match[1]) {
  console.log('Link aus E-Mail gefunden:', match[1]);
  return {
    ratsmitteilungenUrl: match[1],  // Direkt die vollst√§ndige URL
    emailSubject: input.subject,
    emailFrom: input.from,
    emailDate: input.date,
    method: 'Direkt aus E-Mail extrahiert'
  };
}

return {
  ratsmitteilungenUrl: null,
  error: 'Kein Link in E-Mail gefunden'
};

// Suche nach dem _doc/ PDF-Download-Link auf der Seite
const html = $input.first().json.data;

console.log('Suche nach _doc/ PDF-Download-Link...');

// Verschiedene Muster f√ºr _doc/ Links
const docPatterns = [
  // Direct _doc links
  /href="([^"]*\/_doc\/[^"]*)/gi,
  // Escaped _doc links  
  /href=\\"([^"]*\\_doc\\_[^"]*)\\/gi,
  // Button onclick with _doc
  /onclick="[^"]*([^"]*\/_doc\/[^"]*)/gi,
  // Any _doc pattern
  /\/_doc\/(\d+)/gi,
  /\\_doc\\_(\d+)/gi
];

let docUrl = null;
let foundPattern = null;

for (let i = 0; i < docPatterns.length; i++) {
  const matches = [...html.matchAll(docPatterns[i])];
  console.log(`Doc-Pattern ${i + 1}:`, matches.length, 'Treffer');

  if (matches.length > 0) {
    if (matches[0][1]) {
      docUrl = matches[0][1];
    } else {
      // F√ºr reine Nummer-Matches
      docUrl = `/_doc/${matches[0][1]}`;
    }
    foundPattern = `Doc-Pattern ${i + 1}`;
    console.log('Gefundener Doc-Link:', docUrl);
    break;
  }
}

// Absolute URL erstellen
if (docUrl && !docUrl.startsWith('http')) {
  if (docUrl.startsWith('/')) {
    docUrl = `https://www.kaiseraugst.ch${docUrl}`;
  } else {
    docUrl = `https://www.kaiseraugst.ch/${docUrl}`;
  }
}

if (docUrl) {
  return {
    ...($input.first().json),
    pdfDownloadUrl: docUrl,
    foundPattern: foundPattern,
    debugInfo: {
      searchedFor: '_doc/ links',
      found: true
    }
  };
} else {
  return {
    ...($input.first().json),  // ‚Üê Hier war der Fehler: .first).json) statt .first().json)
    pdfDownloadUrl: null,
    error: 'Kein _doc/ Link gefunden',
    debugInfo: {
      searchedFor: '_doc/ links',
      htmlPreview: html.substring(0, 500)
    }
  };
}

// Dateinamen aus verf√ºgbaren Daten generieren
const data = $input.first().json;

console.log('=== DEBUG: Dateiname vorbereiten ===');
console.log('Verf√ºgbare Daten:', Object.keys(data));
console.log('Alle Daten:', JSON.stringify(data, null, 2));

// Versuche verschiedene Datum-Quellen zu finden
let emailDate = null;
let dateStr = null;

// 1. Versuche verschiedene m√∂gliche Datum-Felder
const possibleDateFields = [
  'emailDate', 
  'date', 
  'receivedDate', 
  'sentDate',
  'headers.date'
];

console.log('Suche nach Datum in folgenden Feldern:', possibleDateFields);

for (const field of possibleDateFields) {
  let value = data[field];

  // Handle nested properties like headers.date
  if (field.includes('.')) {
    const parts = field.split('.');
    value = data[parts[0]]?.[parts[1]];
  }

  console.log(`${field}:`, value, typeof value);

  if (value) {
    emailDate = value;
    console.log(`‚úì Datum gefunden in Feld: ${field}`);
    break;
  }
}

// 2. Versuche auch im urspr√ºnglichen Email-Node nachzuschauen
const emailTriggerData = $('Email Trigger (IMAP)').first()?.json;
if (!emailDate && emailTriggerData?.date) {
  emailDate = emailTriggerData.date;
  console.log('‚úì Datum aus Email Trigger gefunden:', emailDate);
}

// 3. Parse das gefundene Datum
if (emailDate) {
  try {
    let parsedDate;

    // Handle verschiedene Datum-Formate
    if (typeof emailDate === 'string') {
      // RFC 2822 Format (typisch f√ºr E-Mails)
      if (emailDate.includes(',') || emailDate.includes('GMT') || emailDate.includes('UTC')) {
        parsedDate = new Date(emailDate);
      }
      // ISO Format
      else if (emailDate.includes('T') && emailDate.includes('Z')) {
        parsedDate = new Date(emailDate);
      }
      // Andere Formate
      else {
        parsedDate = new Date(emailDate);
      }
    } else {
      parsedDate = new Date(emailDate);
    }

    console.log('Geparste Datum:', parsedDate);

    if (!isNaN(parsedDate.getTime())) {
      // Schweizer Zeitzone ber√ºcksichtigen
      const swissDate = new Date(parsedDate.toLocaleString("en-US", {timeZone: "Europe/Zurich"}));
      dateStr = swissDate.toISOString().split('T')[0]; // YYYY-MM-DD
      console.log('‚úì Erfolgreiche Datum-Konvertierung:', dateStr);
    } else {
      console.log('‚ùå Ung√ºltiges Datum nach Parsing');
    }
  } catch (e) {
    console.log('‚ùå Fehler beim Datum parsen:', e.message);
  }
}

// 4. Fallback: Aktuelles Datum verwenden
if (!dateStr) {
  console.log('‚ö†Ô∏è Verwende aktuelles Datum als Fallback');
  const now = new Date();
  const swissNow = new Date(now.toLocaleString("en-US", {timeZone: "Europe/Zurich"}));
  dateStr = swissNow.toISOString().split('T')[0];
}

// 5. Deutsche Formatierung erstellen (DD.MM.YYYY)
let germanDate = '';
let filename = 'Ratsmitteilungen_Unbekanntes_Datum.pdf';

if (dateStr) {
  const [year, month, day] = dateStr.split('-');
  germanDate = `${day}.${month}.${year}`;
  filename = `Ratsmitteilungen_${germanDate}.pdf`;
  console.log('‚úì Generierter Dateiname:', filename);
}

// 6. Optional: Zus√§tzliche Info aus PDF-URL anh√§ngen
if (data.pdfDownloadUrl && data.pdfDownloadUrl.includes('_doc/')) {
  const urlParts = data.pdfDownloadUrl.split('/');
  const lastPart = urlParts[urlParts.length - 1];

  if (lastPart && lastPart.length > 0 && !lastPart.includes('.pdf')) {
    // Nur anh√§ngen wenn es eine Nummer/ID ist, nicht schon eine PDF-Datei
    const baseFilename = `Ratsmitteilungen_${germanDate || dateStr}`;
    filename = `${baseFilename}_${lastPart}.pdf`;
    console.log('‚úì Erweiterte Dateiname mit URL-Info:', filename);
  }
}

// 7. Dateiname bereinigen (problematische Zeichen entfernen)
filename = filename.replace(/[<>:"/\\|?*]/g, '_');

console.log('=== FINAL RESULT ===');
console.log('Final filename:', filename);
console.log('Original email date:', emailDate);
console.log('Processed date string:', dateStr);
console.log('German formatted date:', germanDate);

return {
  ...data,
  filename: filename,
  debugInfo: {
    originalEmailDate: emailDate,
    extractedDate: dateStr,
    germanDate: germanDate,
    finalFilename: filename,
    dateSource: emailDate ? 'email' : 'current',
    allAvailableFields: Object.keys(data)
  }
};

