// Filter video files and prepare for processing
const videoExtensions = ['.mp4', '.mkv', '.avi', '.mov', '.wmv', '.flv', '.webm', '.m4v', '.ts', '.m2ts'];
const items = [];

for (const item of $input.all()) {
  const filePath = item.json.path;
  const fileName = filePath.substring(filePath.lastIndexOf('/') + 1);
  const extension = fileName.toLowerCase().substring(fileName.lastIndexOf('.'));

  if (videoExtensions.includes(extension) && !fileName.startsWith('.')) {
    // Basic episode pattern detection
    const episodePatterns = [
      /s\d{1,2}e\d{1,2}/i,
      /season.?\d{1,2}.?episode.?\d{1,2}/i,
      /\d{1,2}x\d{1,2}/,
      /ep\d{1,2}/i,
      /episode.?\d{1,2}/i
    ];

    const isLikelyEpisode = episodePatterns.some(pattern => pattern.test(fileName));

    items.push({
      originalPath: filePath,
      originalName: fileName,
      extension: extension,
      baseName: fileName.replace(extension, ''),
      size: item.json.size || 0,
      isLikelyEpisode: isLikelyEpisode,
      directory: item.json.directory || item.json.dir || filePath.substring(0, filePath.lastIndexOf('/')) || ''
    });
  }
}

if (items.length === 0) {
  throw new Error('No video files found in the specified directory');
}

return items;

// Process OpenAI response and prepare for Telegram with buttons
const items = [];
let batchNumber = 1;
const batchSize = 3;

for (let i = 0; i < $input.all().length; i += batchSize) {
  const batch = $input.all().slice(i, i + batchSize);
  let message = `ðŸŽ¬ Jellyfin Media Organization - Batch ${batchNumber}\n\n`;

  // Create arrays to store item data for buttons
  const itemIds = [];
  const validItems = [];

  batch.forEach((item, index) => {
    try {
      // Parse OpenAI response
      const aiResponse = JSON.parse(item.json.output.replace(/```json|```/g, '').trim());

      const globalIndex = i + index + 1;
      const confidenceIcon = aiResponse.confidence >= 0.8 ? 'âœ…' : aiResponse.confidence >= 0.6 ? 'âš ï¸' : 'âŒ';
      const typeIcon = aiResponse.type === 'movie' ? 'ðŸŽ¬' : 'ðŸ“º';

      message += `${globalIndex}ï¸âƒ£ ${typeIcon} **CURRENT:** \`${item.json.originalName}\`\n`;

      if (aiResponse.type === 'movie') {
        message += `   ðŸ“ **FOLDER:** Movies/${aiResponse.jellyfin_folder}/\n`;
        message += `   ðŸŽ¥ **FILE:** ${aiResponse.jellyfin_filename.replace('.ext', item.json.extension)}\n`;
      } else if (aiResponse.type === 'episode') {
        message += `   ðŸ“ **SHOW:** TV Shows/${aiResponse.jellyfin_show_folder}/\n`;
        message += `   ðŸ“‚ **SEASON:** ${aiResponse.jellyfin_season_folder}/\n`;

        // Build episode filename
        let episodeFilename = `${aiResponse.show_title}`;
        if (aiResponse.year) episodeFilename += ` (${aiResponse.year})`;
        episodeFilename += ` - S${String(aiResponse.season).padStart(2, '0')}E${String(aiResponse.episode).padStart(2, '0')}`;
        if (aiResponse.episode_title) episodeFilename += ` - ${aiResponse.episode_title}`;
        episodeFilename += item.json.extension;

        message += `   ðŸ“º **FILE:** ${episodeFilename}\n`;
        aiResponse.final_filename = episodeFilename;
      }

      message += `   ${confidenceIcon} **Confidence:** ${Math.round(aiResponse.confidence * 100)}%`;

      if (aiResponse.quality || aiResponse.source) {
        message += ` | **Quality:** ${aiResponse.quality || ''} ${aiResponse.source || ''}`.trim();
      }

      message += `\n\n`;

      // Store processed data
      item.json.aiAnalysis = aiResponse;
      item.json.batchNumber = batchNumber;
      item.json.itemIndex = globalIndex;

      // Add to arrays for buttons (only if no error)
      itemIds.push(globalIndex.toString());
      validItems.push(item);

    } catch (error) {
      message += `${i + index + 1}ï¸âƒ£ **ERROR:** Could not analyze ${item.json.originalName}\n   Error: ${error.message}\n\n`;
      item.json.error = error.message;
      item.json.batchNumber = batchNumber;
      item.json.itemIndex = i + index + 1;
    }
  });

  // Create inline keyboard with buttons
  const keyboard = {
    inline_keyboard: []
  };

  // Add individual item buttons (up to 3 per row)
  if (itemIds.length > 0) {
    const buttonRows = [];
    for (let j = 0; j < itemIds.length; j += 3) {
      const row = itemIds.slice(j, j + 3).map(id => ({
        text: `âœ… ${id}`,
        callback_data: `approve_${batchNumber}_${id}`
      }));
      buttonRows.push(row);
    }
    keyboard.inline_keyboard.push(...buttonRows);
  }

  // Add action buttons
  keyboard.inline_keyboard.push([
    {
      text: "âœ… Approve All",
      callback_data: `approve_all_${batchNumber}`
    },
    {
      text: "â­ï¸ Skip Batch",
      callback_data: `skip_${batchNumber}`
    }
  ]);

  keyboard.inline_keyboard.push([
    {
      text: "ðŸ›‘ Stop Process",
      callback_data: `stop_${batchNumber}`
    }
  ]);

  items.push({
    batchNumber: batchNumber,
    message: message,
    keyboard: keyboard,
    items: batch,
    itemIds: itemIds,
    totalBatches: Math.ceil($input.all().length / batchSize)
  });

  batchNumber++;
}

return items;

// Process user approval decisions from button callbacks
const approvedItems = [];
const telegramResponse = $node['Telegram Response Webhook'].json;
const batchData = $node['Wait for Approval'].json;

// Handle button callback data
let callbackData = '';
let chatId = '';
let isButtonPress = false;

if (telegramResponse.callback_query) {
  // Button was pressed
  callbackData = telegramResponse.callback_query.data;
  chatId = telegramResponse.callback_query.message.chat.id;
  isButtonPress = true;
} else if (telegramResponse.message) {
  // Fallback for text messages (keep for compatibility)
  callbackData = telegramResponse.message.text.toLowerCase();
  chatId = telegramResponse.message.chat.id;
}

// Parse callback data
const parts = callbackData.split('_');
const action = parts[0];
let batchNumber = batchData.batchNumber;

if (parts.length > 1) {
  batchNumber = parseInt(parts[1]) || batchData.batchNumber;
}

if (action === 'stop') {
  // Send confirmation and stop
  return [{
    telegramUpdate: {
      chatId: chatId,
      text: 'ðŸ›‘ Process stopped by user. No files were modified.',
      action: 'stop'
    }
  }];
}

if (action === 'skip') {
  // Skip this batch, continue to next
  return [{
    telegramUpdate: {
      chatId: chatId,
      text: 'â­ï¸ Batch skipped. Moving to next batch...',
      action: 'skip'
    }
  }];
}

if (action === 'approve') {
  const subAction = parts[1];

  if (subAction === 'all') {
    // Approve all items in current batch
    batchData.items.forEach(item => {
      if (!item.json.error) {
        approvedItems.push({
          ...item.json,
          approved: true,
          userDecision: 'all'
        });
      }
    });
  } else {
    // Approve specific item
    const targetBatch = parseInt(parts[1]);
    const itemIndex = parseInt(parts[2]);

    batchData.items.forEach(item => {
      if (!item.json.error && item.json.itemIndex === itemIndex) {
        approvedItems.push({
          ...item.json,
          approved: true,
          userDecision: 'selected'
        });
      }
    });
  }
} else if (!isButtonPress) {
  // Handle text commands for backwards compatibility
  if (callbackData === 'all') {
    batchData.items.forEach(item => {
      if (!item.json.error) {
        approvedItems.push({
          ...item.json,
          approved: true,
          userDecision: 'all'
        });
      }
    });
  } else {
    // Parse specific numbers
    const approvedNumbers = callbackData.match(/\d+/g)?.map(n => parseInt(n)) || [];

    batchData.items.forEach(item => {
      if (!item.json.error && approvedNumbers.includes(item.json.itemIndex)) {
        approvedItems.push({
          ...item.json,
          approved: true,
          userDecision: 'selected'
        });
      }
    });
  }
}

if (approvedItems.length === 0) {
  return [{
    telegramUpdate: {
      chatId: chatId,
      text: 'No items approved in this batch.',
      action: 'none'
    }
  }];
}

// Add telegram update info to first item
approvedItems[0].telegramUpdate = {
  chatId: chatId,
  text: `âœ… ${approvedItems.length} item(s) approved and will be organized.`,
  action: 'approved'
};

return approvedItems;

// Create Jellyfin directory structure and organize files
const fs = require('fs').promises;
const path = require('path');

const JELLYFIN_BASE_PATH = '/path/to/your/jellyfin';
const results = [];

for (const item of $input.all()) {
  try {
    const analysis = item.json.aiAnalysis;
    let targetPath, folderPath;

    if (analysis.type === 'movie') {
      // Movie organization
      folderPath = path.join(JELLYFIN_BASE_PATH, 'Movies', analysis.jellyfin_folder);
      const filename = analysis.jellyfin_filename.replace('.ext', item.json.extension);
      targetPath = path.join(folderPath, filename);

    } else if (analysis.type === 'episode') {
      // TV Show organization
      const showPath = path.join(JELLYFIN_BASE_PATH, 'TV Shows', analysis.jellyfin_show_folder);
      const seasonPath = path.join(showPath, analysis.jellyfin_season_folder);
      folderPath = seasonPath;
      targetPath = path.join(seasonPath, analysis.final_filename);
    }

    // Create directory structure
    await fs.mkdir(folderPath, { recursive: true });

    // Check if target file already exists
    try {
      await fs.access(targetPath);
      results.push({
        ...item.json,
        status: 'skipped',
        reason: 'File already exists',
        targetPath: targetPath
      });
      continue;
    } catch {
      // File doesn't exist, proceed with move
    }

    // Move and rename file
    await fs.rename(item.json.originalPath, targetPath);

    results.push({
      ...item.json,
      status: 'success',
      oldPath: item.json.originalPath,
      newPath: targetPath,
      folderCreated: folderPath,
      type: analysis.type
    });

  } catch (error) {
    results.push({
      ...item.json,
      status: 'error',
      error: error.message
    });
  }
}

return results;

// Generate completion report
const allResults = $input.all();
const successful = allResults.filter(r => r.json.status === 'success');
const skipped = allResults.filter(r => r.json.status === 'skipped');
const errors = allResults.filter(r => r.json.status === 'error');

// Separate by type
const movies = successful.filter(r => r.json.aiAnalysis?.type === 'movie');
const episodes = successful.filter(r => r.json.aiAnalysis?.type === 'episode');

let report = `ðŸŽ‰ **Jellyfin Organization Complete!**\n\n`;
report += `ðŸ“Š **Summary:**\n`;
report += `ðŸŽ¬ Movies organized: ${movies.length}\n`;
report += `ðŸ“º Episodes organized: ${episodes.length}\n`;
report += `â­ï¸ Skipped (already exist): ${skipped.length}\n`;
report += `âŒ Errors: ${errors.length}\n\n`;

if (movies.length > 0) {
  report += `**ðŸŽ¬ Movies Organized:**\n`;
  movies.forEach((item, index) => {
    const movieName = require('path').basename(item.json.newPath);
    report += `${index + 1}. ${movieName}\n`;
  });
  report += `\n`;
}

if (episodes.length > 0) {
  report += `**ðŸ“º Episodes Organized:**\n`;
  // Group episodes by show
  const episodesByShow = {};
  episodes.forEach(item => {
    const showName = item.json.aiAnalysis.show_title;
    if (!episodesByShow[showName]) episodesByShow[showName] = [];
    episodesByShow[showName].push(item);
  });

  Object.keys(episodesByShow).forEach(showName => {
    report += `**${showName}:**\n`;
    episodesByShow[showName].forEach(item => {
      const episodeName = require('path').basename(item.json.newPath);
      report += `  â€¢ ${episodeName}\n`;
    });
  });
  report += `\n`;
}

if (errors.length > 0) {
  report += `**âŒ Errors:**\n`;
  errors.forEach((item, index) => {
    report += `${index + 1}. ${item.json.originalName}: ${item.json.error}\n`;
  });
  report += `\n`;
}

report += `ðŸŽ¬ðŸ“º Don't forget to refresh your Jellyfin libraries!`;

return [{ 
  report: report, 
  summary: { 
    movies: movies.length, 
    episodes: episodes.length, 
    skipped: skipped.length, 
    errors: errors.length,
    total_successful: successful.length
  } 
}];
